package chapter08;import java.util.concurrent.CountDownLatch;/** *  * @author tengfei.fangtf * @version $Id: CountDownLatchTest.java, v 0.1 2015-8-1 ÉÏÎç12:08:52 tengfei.fangtf Exp $ */public class CountDownLatchTest {    static CountDownLatch c = new CountDownLatch(4);    public static void main(String[] args) throws InterruptedException {        Thread t1=new Thread(new Runnable() {            @Override            public void run() {                System.out.println(1);                c.countDown();                System.out.println(2);            }        });         Thread t2=new Thread(new Runnable() {            @Override            public void run() {                System.out.println(3);                c.countDown();                System.out.println(4);            }        });                  Thread t3=new Thread(new Runnable() {            @Override            public void run() {                System.out.println(5);                c.countDown();                System.out.println(6);                c.countDown();            }        });                           Thread t4=new Thread(new Runnable() {            @Override            public void run() {                System.out.println(7);                System.out.println(8);            }        });        t1.start();        t2.start();        t3.start();        t4.start();        c.await();        System.out.println("9");    }}