package chapter08;import java.util.concurrent.BrokenBarrierException;import java.util.concurrent.CyclicBarrier;/** *  * @author tengfei.fangtf * @version $Id: CyclicBarrierTest3.java, v 0.1 2015-8-1 ÉÏÎç12:09:37 tengfei.fangtf Exp $ */public class CyclicBarrierTest3 {    static CyclicBarrier c = new CyclicBarrier(2);    public static void main(String[] args) throws InterruptedException, BrokenBarrierException {        Thread thread = new Thread(new Runnable() {            @Override            public void run() {                try {                    System.out.println("before await");                    c.await();                } catch (Exception e) {                }            }        });        thread.start();        System.out.println(        thread.getState());        System.out.println(thread.getContextClassLoader());        thread.interrupt();        try {            c.await();            System.out.println("after 2nd await");        } catch (Exception e) {            System.out.println(c.isBroken());        }finally {            System.out.println(thread.isInterrupted());            System.out.println(thread.isAlive());            System.out.println(thread.isDaemon());            System.out.println(thread.getState());        }    }}